import rootReducer from "../reducer";
import { createStore } from "redux";
import { TSample } from "../reducer/sample";
import {Storage, persistReducer, PersistConfig} from 'redux-persist'
import { composeWithDevTools } from 'redux-devtools-extension'

export type TStore = {
    sample: TSample
}

const persistconfig = {
    key: 'root',
    Storage,
}

const persistedRedur = persistReducer(persistconfig, reducers(history))

const store: TStore = createStore(
    rootReducer,
    composeWithDevTools()
)

export default store
